describe('Setup Guide Generation', () => {
  it('should generate appropriate setup steps', () => {
    const guide = getSetupGuide();

    expect(guide.steps).toBeInstanceOf(Array);
    expect(guide.steps.length).toBeGreaterThan(0);
    expect(guide.steps[0]).toHaveProperty('title');
    expect(guide.steps[0]).toHaveProperty('description');
  });

  it('should customize guide based on missing configuration', () => {
    delete process.env.GITHUB_TOKEN;

    const guide = getSetupGuide();
    const tokenStep = guide.steps.find(step => 
      step.title.includes('GitHub Token')
    );

    expect(tokenStep).toBeDefined();
  });
});
