    expect(healthScore).toBeGreaterThan(0);
    expect(healthScore).toBeLessThanOrEqual(100);
  });

  it('should penalize inactive repositories', () => {
    const activeRepo = {
      updated_at: new Date().toISOString(),
      stargazers_count: 50
    };

    const inactiveRepo = {
      updated_at: new Date(Date.now() - 365 * 24 * 60 * 60 * 1000).toISOString(),
      stargazers_count: 50
    };

    const activeScore = calculateHealthScore(activeRepo);
    const inactiveScore = calculateHealthScore(inactiveRepo);

    expect(activeScore).toBeGreaterThan(inactiveScore);
  });
});
```

