// src/lib/utils/search.ts
export interface SearchOptions {
  query: string;
  filters: {
    state?: 'open' | 'closed' | 'all';
    labels?: string[];
    priority?: string[];
    author?: string;
    dateRange?: {
      start: Date;
      end: Date;
    };
  };
  sortBy?: 'created' | 'updated' | 'priority';
  sortOrder?: 'asc' | 'desc';
}

export const searchIssues = (
  issues: Issue[], 
  options: SearchOptions
): SearchResult => {
  let filteredIssues = issues;

  // ãƒ†ã‚­ã‚¹ãƒˆæ¤œç´¢
  if (options.query) {
    filteredIssues = filteredIssues.filter(issue =>
      issue.title.toLowerCase().includes(options.query.toLowerCase()) ||
      issue.body?.toLowerCase().includes(options.query.toLowerCase())
    );
  }

  // ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
  if (options.filters.state && options.filters.state \!== 'all') {
    filteredIssues = filteredIssues.filter(issue => 
      issue.state === options.filters.state
    );
  }

  return {
    issues: filteredIssues,
    totalCount: filteredIssues.length,
    searchTime: Date.now()
  };
};
