import { writeFileSync, mkdirSync } from 'fs';
import { join } from 'path';
import { fetchGitHubIssues } from '../src/lib/github/issues';
import { getConfig } from '../src/lib/config';

async function fetchAndSaveData() {
  try {
    const config = getConfig();
    const issuesResult = await fetchGitHubIssues(config.github);

    if (\!issuesResult.success) {
      throw new Error(`GitHub API ã‚¨ãƒ©ãƒ¼: ${issuesResult.error.message}`);
    }

    // ãƒ‡ãƒ¼ã‚¿ãƒ‡ã‚£ãƒ¬ã‚¯ãƒˆãƒªã®ä½œæˆ
    const dataDir = join(process.cwd(), 'src/data/github');
    mkdirSync(dataDir, { recursive: true });

    // Issues ãƒ‡ãƒ¼ã‚¿ã®ä¿å­˜
    writeFileSync(
      join(dataDir, 'issues.json'),
      JSON.stringify(issuesResult.data, null, 2)
    );

    // ãƒ¡ã‚¿ãƒ‡ãƒ¼ã‚¿ã®ä¿å­˜
    writeFileSync(
      join(dataDir, 'metadata.json'),
      JSON.stringify({
        lastUpdated: new Date().toISOString(),
        totalIssues: issuesResult.data.length,
        openIssues: issuesResult.data.filter(i => i.state === 'open').length,
        closedIssues: issuesResult.data.filter(i => i.state === 'closed').length
      }, null, 2)
    );

    console.log('âœ… GitHub ãƒ‡ãƒ¼ã‚¿ã®å–å¾—ã¨ä¿å­˜ãŒå®Œäº†ã—ã¾ã—ãŸ');
  } catch (error) {
    console.error('âŒ ãƒ‡ãƒ¼ã‚¿å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
    process.exit(1);
  }
}

fetchAndSaveData();
