describe('GitHub API Validation', () => {
  it('should validate GitHub token permissions', async () => {
    nock('https://api.github.com')
      .get('/user')
      .reply(200, { login: 'testuser' });

    const result = await performAdditionalValidation();
    expect(result.github.tokenValid).toBe(true);
    expect(result.github.permissions).toContain('read');
  });

  it('should detect invalid tokens', async () => {
    nock('https://api.github.com')
      .get('/user')
      .reply(401, { message: 'Bad credentials' });

    const result = await performAdditionalValidation();
    expect(result.github.tokenValid).toBe(false);
  });

  it('should validate repository access', async () => {
    nock('https://api.github.com')
      .get('/repos/owner/repo')
      .reply(200, { full_name: 'owner/repo' });

    const result = await performAdditionalValidation();
    expect(result.repository.accessible).toBe(true);
  });
});
```

